import Box from './components/Box'
import Grid from './components/Grid'
import { useState, useEffect } from 'react'
function App() {
  const [boxes] = useState([
    {id: 0, name : 'box1', clicked: false},
    {id: 1, name : 'box2', clicked: false},
    {id: 2, name : 'box3', clicked: false},
    {id: 3, name : 'box4', clicked: false}

])
  const [buttonClicked] = useState(false)
  const clickBox = (id, buttonClicked) => {
    let currentBox
    for (let i = 0; i < boxes.length; i++) {
      currentId = boxes[i].id
      if(currentId === id){
        console.log("box found")
        currentBox.clicked = !currentBox.clicked
      }
    } 
    console.log("CLICKED  button!!!!", id)
    buttonClicked = !buttonClicked

  }
  return (
    <div className="App">
      <h1>hello</h1>
      <Grid clickBox={clickBox} buttonClicked={buttonClicked} boxes = {boxes}/>
    </div>
  );
}

export default App;
